import React from 'react';
import { useRouter } from 'next/router';

const ProductItem = () => {
    const { query: { id }} = useRouter();
    return (
        <div>
         Hola, producto item dinamico ;)
    con el ID: <h1>{id}</h1>
        </div>
    );
};

export default ProductItem;
